// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity >=0.7.5;
pragma abicoder v2;

import '@uniswap/v3-core/contracts/interfaces/callback/IUniswapV3SwapCallback.sol';

/// @title Router token swapping functionality
/// @notice Functions for swapping tokens via Uniswap V3
interface ISwapRouter is IUniswapV3SwapCallback {
    struct ExactInputSingleParams {
        address tokenIn;
        address tokenOut;
        uint24 fee;
        address recipient;
        uint256 deadline;
        uint256 amountIn;
        uint256 amountOutMinimum;
        uint160 sqrtPriceLimitX96;
    }

    /// @notice Swaps `amountIn` of one token for as much as possible of another token
    /// @param params The parameters necessary for the swap, encoded as `ExactInputSingleParams` in calldata
    /// @return amountOut The amount of the received token
    function exactInputSingle(ExactInputSingleParams calldata params) external payable returns (uint256 amountOut);

    struct ExactInputParams {
        bytes path;
        address recipient;
        uint256 deadline;
        uint256 amountIn;
        uint256 amountOutMinimum;
    }

    /// @notice Swaps `amountIn` of one token for as much as possible of another along the specified path
    /// @param params The parameters necessary for the multi-hop swap, encoded as `ExactInputParams` in calldata
    /// @return amountOut The amount of the received token
    function exactInput(ExactInputParams calldata params) external payable returns (uint256 amountOut);

    struct ExactOutputSingleParams {
        address tokenIn;
        address tokenOut;
        uint24 fee;
        address recipient;
        uint256 deadline;
        uint256 amountOut;
        uint256 amountInMaximum;
        uint160 sqrtPriceLimitX96;
    }

    /// @notice Swaps as little as possible of one token for `amountOut` of another token
    /// @param params The parameters necessary for the swap, encoded as `ExactOutputSingleParams` in calldata
    /// @return amountIn The amount of the input token
    function exactOutputSingle(ExactOutputSingleParams calldata params) external payable returns (uint256 amountIn);

    struct ExactOutputParams {
        bytes path;
        address recipient;
        uint256 deadline;
        uint256 amountOut;
        uint256 amountInMaximum;
    }

    /// @notice Swaps as little as possible of one token for `amountOut` of another along the specified path (reversed)
    /// @param params The parameters necessary for the multi-hop swap, encoded as `ExactOutputParams` in calldata
    /// @return amountIn The amount of the input token
    function exactOutput(ExactOutputParams calldata params) external payable returns (uint256 amountIn);
}

contracts/FlashLoanArbitrageur.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IPool} from "@aave/core-v3/contracts/interfaces/IPool.sol";
import {IFlashLoanSimpleReceiver} from "@aave/core-v3/contracts/flashloan/interfaces/IFlashLoanSimpleReceiver.sol";
import {IPoolAddressesProvider} from "@aave/core-v3/contracts/interfaces/IPoolAddressesProvider.sol";
import {ISwapRouter} from "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
using Strings for uint256;

contract FlashLoanArbitrageur is IFlashLoanSimpleReceiver {
    struct ArbitrageParams {
        address tokenMid;
        uint24 fee1;
        uint24 fee2;
        uint256 minProfit;
        uint8 direction; // 0: Uniswap->Sushi, 1: Sushi->Uniswap
    }

    address public owner;
    IPool public immutable aavePool;
    IPoolAddressesProvider public immutable addressesProvider;
    ISwapRouter public immutable uniswapRouter;
    ISwapRouter public immutable sushiswapRouter;

    event ArbitrageExecuted(address indexed initiator, address asset, uint256 profit);
    event Withdraw(address indexed token, uint256 amount);
    event Debug(string message);

    modifier onlyOwner() {
        require(msg.sender == owner, "Not owner");
        _;
    }

    constructor(
        address _aavePool,
        address _addressesProvider,
        address _uniswapRouter,
        address _sushiswapRouter
    ) {
        owner = msg.sender;
        aavePool = IPool(_aavePool);
        addressesProvider = IPoolAddressesProvider(_addressesProvider);
        uniswapRouter = ISwapRouter(_uniswapRouter);
        sushiswapRouter = ISwapRouter(_sushiswapRouter);
    }

    // Obligatoire pour interface Aave V3
    function ADDRESSES_PROVIDER() external view override returns (IPoolAddressesProvider) {
        return addressesProvider;
    }
    function POOL() external view override returns (IPool) {
        return aavePool;
    }


    // --- Variables ---
    // (Déjà déclarées plus haut)

    // --- LOGIQUE PRINCIPALE D'ARBITRAGE ---

    /**
     * @notice Lance un arbitrage flash loan avec les paramètres du struct ArbitrageParams
     * @dev Seul le owner peut appeler cette fonction
     */
    function executeArbitrage(
        address asset,
        uint256 amount,
        address tokenMid,
        uint24 fee1,
        uint24 fee2,
        uint256 minProfit,
        uint8 direction
    ) external onlyOwner {
        emit Debug("Start executeArbitrage");
        ArbitrageParams memory arb = ArbitrageParams({
            tokenMid: tokenMid,
            fee1: fee1,
            fee2: fee2,
            minProfit: minProfit,
            direction: direction
        });
        bytes memory params = abi.encode(arb);
        aavePool.flashLoanSimple(
            address(this),
            asset,
            amount,
            params,
            0 // referralCode
        );
    }


    /**
     * @notice Callback Aave V3. Effectue les swaps et rembourse le prêt.
     * @dev Respecte la signature officielle IFlashLoanSimpleReceiver.
     */
    function executeOperation(
        address asset,
        uint256 amount,
        uint256 premium,
        address initiator,
        bytes calldata params
    ) external override returns (bool) {
        emit Debug("Start executeOperation");
        if (msg.sender != address(aavePool)) {
            emit Debug("FAIL: Not called by Aave pool");
        }
        require(msg.sender == address(aavePool), "Only Aave pool can call");
        if (initiator != address(this)) {
            emit Debug("FAIL: Not initiated by contract");
        }
        require(initiator == address(this), "Not initiated by contract");
        emit Debug("Avant _doArbitrage");
        return _doArbitrage(asset, amount, premium, initiator, params);
    }

    function _doArbitrage(
        address asset,
        uint256 amount,
        uint256 premium,
        address initiator,
        bytes memory arbBytes
    ) internal returns (bool) {
        emit Debug("Start _doArbitrage");
        ArbitrageParams memory arb = abi.decode(arbBytes, (ArbitrageParams));
        emit Debug("Before first swap");
        uint256 amountMid = arb.direction == 0
            ? _swap(uniswapRouter, asset, arb.tokenMid, arb.fee1, amount)
            : _swap(sushiswapRouter, asset, arb.tokenMid, arb.fee2, amount);

        emit Debug("Before second swap");
        uint256 amountBack = arb.direction == 0
            ? _swap(sushiswapRouter, arb.tokenMid, asset, arb.fee2, amountMid)
            : _swap(uniswapRouter, arb.tokenMid, asset, arb.fee1, amountMid);

        uint256 totalOwed = amount + premium;
        if (amountBack < totalOwed + arb.minProfit) {
            emit Debug("FAIL: Not enough profit");
        }
        require(amountBack >= totalOwed + arb.minProfit, "Not enough profit");
        emit ArbitrageExecuted(initiator, asset, amountBack - totalOwed);
        emit Debug("Arbitrage finished successfully");
        return true;
    }

    function _swap(
        ISwapRouter router,
        address tokenIn,
        address tokenOut,
        uint24 fee,
        uint256 amountIn
    ) private returns (uint256) {
        IERC20(tokenIn).approve(address(router), amountIn);
        return router.exactInputSingle(
            ISwapRouter.ExactInputSingleParams({
                tokenIn: tokenIn,
                tokenOut: tokenOut,
                fee: fee,
                recipient: address(this),
                deadline: block.timestamp,
                amountIn: amountIn,
                amountOutMinimum: 0,
                sqrtPriceLimitX96: 0
            })
        );
    }

    /**
     * @notice Retire les profits du contrat
     */
    function withdraw(address token) external onlyOwner {
        uint256 bal = IERC20(token).balanceOf(address(this));
        require(bal > 0, "Nothing to withdraw");
        IERC20(token).transfer(owner, bal);
        emit Withdraw(token, bal);
    }
}

